using System.Collections; using System.Collections.Generic; using UnityEngine;  public class DropSpike : MonoBehaviour {         bool drop = false;     [SerializeField] float dropspeed = 5;     [SerializeField] float addspeed = 1f;     float timer;       private void Update()     {         if (drop)         {             transform.Translate(Vector3.down * Time.deltaTime*dropspeed);              dropspeed += Time.deltaTime*addspeed;             if (timer < Time.time) {Destroy(gameObject); }         }      }     private void OnTriggerEnter2D(Collider2D other)     {         if (other.gameObject.layer == LayerMask.NameToLayer("Player"))         {             drop = true;             timer = Time.time+5f;         }     } } 